import { useState } from "react";

function AddChallenge() {
    
    const [month,setMonth] = useState('');
    const [description,setDescription] = useState('');

    return(
        <div>
        <div>
            <label htmlFor="month">Month</label>
            <input type="text" id="month" value={month} onChange={(e) => setMonth(e,EventTarget.value)}>Required</input>
        </div>
        <div>
            <label htmlFor="description">Description</label>
            <textarea id="description" value={description} onChange={(e) => setDescription(e,EventTarget.value)}>Required</textarea>
        </div>
        </div>
    );
}

export default AddChallenge;
